Get-Mailbox | Get-MailboxPermission | Where-Object {$_.user.tostring() -ne "NT AUTHORITY\SELF" -and $_.IsInherited -eq $false} | Select-Object Identity,User,AccessRights | Export-Csv -NoTypeInformation c:\admin\alignment\MailboxDelegates.csv # | Select Identity,User,@{Name='AccessRights';Expression={[string]::join(', ', $_.AccessRights)}} | Export-Csv -NoTypeInformation c:\admin\alignment\MailboxDelegates.csv